import axios from "axios";
export default {
  name: "prestataireUpdateStand",
  data: () => {
    return {
      id_user: null,
      id: null,
      stand: {},
      name: null,
      description: null,
      types: null,
      type: null
    };
  },
  methods: {
    goTo(path) {
      this.$router.replace(path);
    },
    update() {
      let error = false;
      if (!this.name) {
        document.querySelector('#name').style.backgroundColor = 'red';
        error = true;
      }
      if (!this.type) {
        document.querySelector('#type').style.backgroundColor = 'red';
        error = true;
      }
      if (!error) {
        axios({
          method: 'post',
          url: 'http://localhost:3000/prestataires/' + this.id_user + '/stand/' + this.id + '/update',
          data: {
            name: this.name,
            description: this.description,
            type: this.type
          }
        }).then(result => {
          this.$store.commit('setMessage', result.data);
        }).catch(error => {
          this.message = error;
          console.log(error);
        });
      }
    }
  },
  mounted() {
    this.id_user = this.$store.state.user.id;
    this.id = this.$route.params.id;
    axios({
      method: 'get',
      url: 'http://localhost:3000/prestataires/' + this.id_user + '/stand/' + this.id
    }).then(result => {
      this.stand = result.data.data.stand;
      this.types = result.data.data.types;
      this.name = this.stand.name;
      this.description = this.stand.description;
      this.type = this.stand.type;
    }).catch(error => {
      console.log(error);
    });
  }
};
