import axios from "axios";
export default {
  name: "prestataireAddManege",
  data: () => {
    return {
      name: null,
      description: null,
      types: null,
      type: null,
      taille: null,
      id_user: null
    };
  },
  methods: {
    goTo(path) {
      this.$router.replace(path);
    },
    add() {
      let error = false;
      if (!this.name) {
        document.querySelector('#name').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#name').style.borderColor = 'green';
      }
      if (!this.description) {
        document.querySelector('#description').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#description').style.borderColor = 'green';
      }
      if (this.type === null) {
        document.querySelector('#type').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#type').style.borderColor = 'green';
      }
      if (!this.taille) {
        document.querySelector('#taille').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#taille').style.borderColor = 'green';
      }
      if (!error) {
        axios({
          method: 'post',
          url: 'http://localhost:3000/prestataires/' + this.id_user + '/addManege',
          data: {
            name: this.name,
            description: this.description,
            type: this.type,
            taille_min: this.taille
          }
        }).then(result => {
          this.goTo('/prestataire/maneges');
          this.$store.commit('setMessage', result.data);
        }).catch(error => {
          console.log(error);
        });
      }
    }
  },
  mounted() {
    this.id_user = this.$store.state.user.id;
    axios({
      method: 'get',
      url: 'http://localhost:3000/users/typesManege'
    }).then(result => {
      this.types = result.data.data;
    }).catch(error => {
      console.log(error);
    });
  }
};
