import axios from "axios";
import { getGoogleUrl } from "@/utils/getGoogleUrl";
export default {
  name: "userLogin",
  data: () => ({
    firstname: null,
    surname: null,
    password: null,
    password2: null,
    email: null
  }),
  methods: {
    getGoogleUrl,
    signup() {
      document.querySelector('#signup').style.display = 'block';
    },
    signupPost() {
      if (this.validateLogin() && this.validateSignup()) {
        axios({
          method: 'post',
          url: 'http://localhost:3000/users/signup',
          data: {
            firstname: this.firstname,
            surname: this.surname,
            password: this.password,
            email: this.email
          }
        }).then(result => {
          location.reload();
          this.$store.commit('setMessage', {
            success: 1,
            data: result.data.data.data
          });
        }).catch(error => {
          console.log(error);
        });
      }
    },
    login() {
      if (this.validateLogin()) {
        axios({
          method: 'post',
          url: 'http://localhost:3000/users/login',
          data: {
            firstname: this.firstname,
            surname: this.surname,
            password: this.password
          }
        }).then(result => {
          let data = result.data.data;
          let token = result.data.data.token;
          axios.defaults.headers.common['Authorization'] = token;
          this.$store.commit('setMessage', {
            success: data.success,
            data: data.message
          });
          if (data.data.length !== 0) {
            this.$store.commit('setUser', data.data);
            let role = data.data.role;
            if (role === 'user') this.$router.replace('/');else this.$router.replace('/' + data.data.role);
          }
        }).catch(error => {
          console.log(error);
        });
      }
    },
    validateLogin() {
      let valid = true;
      if (!this.firstname) {
        document.querySelector('#firstname').style.borderColor = 'red';
        valid = false;
      } else document.querySelector('#firstname').style.borderColor = 'black';
      if (!this.surname) {
        document.querySelector('#surname').style.borderColor = 'red';
        valid = false;
      } else document.querySelector('#surname').style.borderColor = 'black';
      if (!this.password) {
        document.querySelector('#password').style.borderColor = 'red';
        valid = false;
      } else document.querySelector('#password').style.borderColor = 'black';
      return valid;
    },
    validateSignup() {
      let valid = true;
      if (!this.password2 || this.password !== this.password2) {
        document.querySelector('#password2').style.borderColor = 'red';
        valid = false;
      } else document.querySelector('#password2').style.borderColor = 'black';
      if (!this.email || !this.email.includes("@")) {
        document.querySelector('#email').style.borderColor = 'red';
        valid = false;
      } else document.querySelector('#email').style.borderColor = 'black';
      return valid;
    },
    google() {
      document.location.href = getGoogleUrl('/');
    }
  },
  mounted() {
    let user = this.$store.state.user;
    if (user && user.role !== 'user') this.$router.replace('/' + user.role);
  }
};
