import axios from "axios";
export default {
  name: "notesStar",
  props: {
    type: null,
    id: null,
    rate: null
  },
  data: () => {
    return {
      note: null,
      notes: []
    };
  },
  computed: {
    rangeNote() {
      return (this.note * 0.05).toFixed(1);
    },
    rateAVG() {
      return this.rate.toFixed(1);
    }
  },
  methods: {
    evaluate() {
      let id_user = this.$store.state.user.id;
      axios({
        method: 'post',
        url: 'http://localhost:3000/users/note/' + id_user + '/' + this.type + '/' + this.id + '/' + this.rangeNote
      }).then(result => {
        console.log(result);
      }).catch(error => {
        console.log(error);
      });
    }
  },
  mounted() {
    if (!this.rate) this.note = 0;else this.note = this.rateAVG / 0.05;
    axios({
      method: 'get',
      url: 'http://localhost:3000/users/notes/' + this.$store.state.user.id
    }).then(result => {
      console.log(result.data.data);
    }).catch(error => {
      console.log(error);
    });
  }
};
