import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "prestataireAddArtiste",
  data: () => {
    return {
      name: null,
      description: null,
      types: null,
      type: null,
      groupe: [],
      id_user: null
    };
  },
  methods: {
    goTo(path) {
      this.$router.replace(path);
    },
    getData() {
      this.id_user = this.$store.state.user.id;
      axios({
        method: 'get',
        url: 'http://localhost:3000/users/typesArtiste'
      }).then(result => {
        this.types = result.data.data;
      }).catch(error => {
        console.log(error);
      });
    },
    addMember() {
      this.groupe.push(document.querySelector('#groupe').value);
    },
    deleteMember(index) {
      this.groupe.splice(index, 1);
    },
    add() {
      let error = false;
      if (!this.name) {
        document.querySelector('#name').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#name').style.borderColor = 'green';
      }
      if (!this.description) {
        document.querySelector('#description').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#description').style.borderColor = 'green';
      }
      if (!this.type) {
        document.querySelector('#type').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#type').style.borderColor = 'green';
      }
      if (this.groupe.length === 0) {
        document.querySelector('#groupe').style.borderColor = 'red';
        error = true;
      } else {
        document.querySelector('#groupe').style.borderColor = 'green';
      }
      if (!error) {
        axios({
          method: 'post',
          url: 'http://localhost:3000/prestataires/' + this.id_user + '/addArtiste',
          data: {
            name: this.name,
            description: this.description,
            type: this.type,
            groupe: this.groupe
          }
        }).then(result => {
          this.goTo('/prestataire/artistes');
          this.$store.commit('setMessage', result.data);
        }).catch(error => {
          console.log(error);
        });
      }
    }
  },
  mounted() {
    this.getData();
  }
};
